// 1. Install Dependencies
// 2. Import Libraries
// 3. Setup webcam and canvas
// 4. Define references to those
// 5. Load handpose
// 6. Detect function
// 7. Drawing utilities from tensorflow
// 8. Draw functions

import React, {useRef} from 'react';
import * as tf from "@tensorflow/tfjs";
import * as handpose from '@tensorflow-models/handpose';
import Webcam from 'react-webcam';

//import logo from './logo.svg';
import './App.css';

function App() {

  const webcamRef = useRef(null);
  const canvasRef = useRef(null);
  return (
    <div className="App">
      <header className="App-header">
        <Webcam 
        ref={webcamRef}
        style={{
            position:'absolute',
            marginLeft:"auto",
            marginRight:'auto',
            left:0,
            right:0,
            textAlign:"center",
            zIndex:9,
            width:640,
            height:480,
          }}
        />
        <canvas
          ref={canvasRef}
          style={{
            position:'absolute',
            marginLeft:"auto",
            marginRight:'auto',
            left:0,
            right:0,
            textAlign:"center",
            zIndex:9,
            width:640,
            height:480,
            }}
        />
      </header>
    </div>
  );
}

export default App;
